import * as React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import './Home.css';

class Home extends React.Component {
    render() {
        return (
            <>
                <h2>HOME</h2>
                <form action="/validate" method="post" encType="multipart/form-data" id="indexform" name="indexform" onReset={this.setReset()}>

                    <fieldset>
                        <legend>File DATA<sub>(*.csv)</sub><span className="text-danger font-weight-bold">*</span>:</legend>
                        <input type="file" name="csvFile" id="csvFile" required="" />
                    </fieldset>

                    <fieldset>
                        <legend>File VAL<sub>(*.csv)</sub>:</legend>

                        <input type="hidden" name="valMethod" value="file" />

                        <div id="valFileMethod">
                            <input type="file" name="valFile" id="valFile" />
                            <a role="button" href="/apps/csv_valed/pages/val_generator.php">
                                <button type="button" className="btn btn-warning" id="go_home">
                                    <i className="bi bi-gear-fill"></i> Generate new one
                                </button>
                            </a>
                        </div>

                    </fieldset>
                    <fieldset id="table_style_cont">
                        <legend>Table result look</legend>
                        <input type="radio" name="tablestyle" id="light" value="light" /><label htmlFor="light">light</label>
                        <input type="radio" name="tablestyle" id="dark" value="dark" defaultChecked /><label htmlFor="dark">dark</label>
                    </fieldset>

                    <fieldset>
                        <legend>Enclosures</legend>
                        <div className="row">
                            <label htmlFor="dataenclosure" className="col-5">Data file</label>
                            <input type="text" id="dataenclosure" name="dataenclosure" maxLength="1" value="&quot;" data-original-value="," required="" className="col-2" />
                        </div>

                        <div className="row">
                            <label htmlFor="valenclosure" className="col-5">Val file</label>
                            <input type="text" id="valenclosure" name="valenclosure" maxLength="1" value="&quot;" data-original-value="," required="" className="col-2" data-default-readonly="false" />
                            <div id="lockEnclosures" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button">
                                <i className="bi bi-lock-fill" aria-hidden="true"></i>
                                <i className="bi bi-unlock-fill" aria-hidden="true" style={{ display: "none" }}></i>
                            </div>
                        </div>

                        <div id="enclosuresMsg"></div>
                    </fieldset>
                    <fieldset>
                        <legend>Delimiters</legend>
                        <div className="row">
                            <label htmlFor="datadelimiter" className="col-5">Data file</label>
                            <input type="text" id="datadelimiter" name="datadelimiter" maxLength="1" value="," data-original-value="," required="" className="col-2" />
                        </div>
                        <div className="row">
                            <label htmlFor="valdelimiter" className="col-5">Val file</label>
                            <input type="text" id="valdelimiter" name="valdelimiter" maxLength="1" value="," data-original-value="," required="" className="col-2" data-default-readonly="false" />
                            <div id="lockDelimiters" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button">
                                <i className="bi bi-lock-fill" aria-hidden="true"></i>
                                <i className="bi bi-unlock-fill" aria-hidden="true" style={{ display: "none" }}></i>
                            </div>
                        </div>

                        <div className="row" id="delimitersMsg"></div>
                    </fieldset>

					<div style={{marginTop:"2em !important", textAlign: "left"}}>
						<input type="submit" value="Validate" name="submit" className="btn btn-primary" />
						<input type="reset" value="Clear" className="resetter btn btn-outline-warning" />
					</div> 
                </form>


				<script src="/scripts/index.js" />
            </>
        );
    }




}



export default Home;



// gestione delle icone da mostrare
function lockIconsDelimiters() {
	if ($("#valdelimiter").prop("disabled") == true) {
		$("#lockDelimiters .fa-unlock").show();
		$("#lockDelimiters .fa-lock").hide();
	} else {
		$("#lockDelimiters .fa-lock").show();
		$("#lockDelimiters .fa-unlock").hide();
	}
}

function lockIconsEnclosures() {
	if ($("#valenclosure").prop("disabled") == true) {
		$("#lockEnclosures .fa-unlock").show();
		$("#lockEnclosures .fa-lock").hide();
	} else {
		$("#lockEnclosures .fa-lock").show();
		$("#lockEnclosures .fa-unlock").hide();
	}
}


// gestione lucchetti
// ...disabilita campi e controlla il carattere |
function lockEnclosures() {
	$('#lockEnclosures').click(function () {
		$("#valenclosure").val($("#dataenclosure").val());
		$("#valenclosure").prop("disabled", !$("#valenclosure").prop("disabled"));

		if ($("#valenclosure").prop("disabled") == true) {
			syncroDelimiterLookFields();

		}
		$('#enclosuresMsg .close').trigger('click');

		if ($('#valenclosure').val() == "|") {
			let msg = "if <em>enclosure</em> is <em>\"|\"<em>, the validation may not work.";
			showAlert($("#enclosuresMsg"), msg);
		}

		lockIconsEnclosures();
	});
}

// ...disabilita campi e controlla il carattere |
function lockDelimiters() {
	$('#lockDelimiters').click(function () {
		$("#valdelimiter").val($("#datadelimiter").val());
		$("#valdelimiter").prop("disabled", !$("#valdelimiter").prop("disabled"));

		if ($("#valdelimiter").prop("disabled") == true) {
			syncroDelimiterLookFields();
		}
		$('#delimitersMsg .close').trigger('click');

		if ($('#valdelimiter').val() == "|") {
			let msg = "if <em>valdelimiter</em> is <em>\"|\"<em>, the validation may not work.";
			showAlert($("#delimitersMsg"), msg);
		}

		lockIconsDelimiters();
	})
}

// ...sincronizzazione campi enclosures bloccati
function syncroEnclosuresLookFields() {
	$('#dataenclosure').keyup(function () {
