import React, { useState, useRef } from 'react';
// import ReactDOM from 'react-dom/client';

import { setReset } from '../Scripts/Home.js'

import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';



function Home() {

	const [formValues, setFormValues] = useState({
		csvFile: "",
		valMethod: "file",
		valFile: "",
		tablestyle: "black",
		dataenclosure: "",
		valenclosure: "",
		datadelimiter: ",",
		valdelimiter: ",",
	});

	const [isFormVisible, setIsFormVisible] = useState(true);

	const inputFileRef = useRef();

	const handleChange = (event) => {
		const { name, value } = event.target;
		console.log(name, value);

		setFormValues({ ...formValues, [name]: value });
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formValues);
		console.log(inputFileRef?.current?.files);
		setIsFormVisible(false);
	};

	return (
		<>
			<h2>HOME</h2>
			<form action="/validate" onSubmit={handleSubmit} className="text-start">

				<div className="row mb-3">
					<label className="col-3">File DATA<sub>(*.csv)</sub><span className="text-danger font-weight-bold">*</span>:</label>
					<input type="file" name="csvFile" id="csvFile" name="name" className="col-9" value={formValues?.csvFile} onChange={handleChange} required />
				</div>




				<div className="row mb-3">
					<label className='col-3'>File VAL<sub>(*.csv)</sub>:</label>

					<div id="valFileMethod" className="col-9 text-start">
						<input type="file" name="valFile" id="valFile" />
						<a role="button" href="/val-generator">
							<button type="button" className="btn btn-warning" id="go_home">
								<i className="bi bi-gear-fill"></i> Generate new one
                                </button>
						</a>
					</div>

					<input type="hidden" name="valMethod" />
				</div>


				<div id="table_style_cont">
					<label>Table result look</label>
					<input type="radio" name="tablestyle" id="light" value="light" /><label htmlFor="light" style={{ marginRight: "1em" }}>light</label>
					<input type="radio" name="tablestyle" id="dark" value="dark" defaultChecked /><label htmlFor="dark">dark</label>
				</div>


				<div className="">
					<label>Enclosures</label>
					<div className="row">
						<label htmlFor="dataenclosure" className="col-2">Data file</label>
						<input type="text" id="dataenclosure" name="dataenclosure" data-original-value="," required="" className="col-4" />
					</div>

					<div className="row">
						<label htmlFor="valenclosure" className="col-2">Val file</label>
						<input type="text" id="valenclosure" name="valenclosure" data-original-value="," required="" className="col-4" data-default-readonly="false" />
						<div id="lockEnclosures" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
							<i className="bi bi-lock-fill" ><span className="fs-6">locked</span></i>
							<i className="bi bi-unlock-fill ms-1"><span className="fs-6">unlocked</span></i>
						</div>
					</div>

					<div id="enclosuresMsg"></div>
				</div>


				<div className="col-6">
					<label>Delimiters</label>
					<div className="row">
						<label htmlFor="datadelimiter" className="col-2">Data file</label>
						<input type="text" id="datadelimiter" name="datadelimiter" defaultValue="," data-original-value="," required="" className="col-4" />
					</div>
					<div className="row">
						<label htmlFor="valdelimiter" className="col-2">Val file</label>
						<input type="text" id="valdelimiter" name="valdelimiter" defaultValue="," data-original-value="," required="" className="col-4" data-default-readonly="false" />
						<div id="lockDelimiters" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
							<i className="bi bi-lock-fill" ><span className="fs-6">locked</span></i>
							<i className="bi bi-unlock-fill ms-1"><span className="fs-6">unlocked</span></i>
						</div>
					</div>

					<div className="row" id="delimitersMsg"></div>
				</div>


				<div className="col-6">
					<input type="submit" value="Validate" name="submit" className="btn btn-primary me-3" />
					<input type="reset" value="Clear" className="resetter btn btn-secondary me-3" onClick={() => { setReset() }} />
				</div>
			</form>

		</>
	);


}


export default Home;
