import React, { useState, useRef } from 'react';
// import ReactDOM from 'react-dom/client';

import Validator from './Validator.js'

import { setReset } from '../Scripts/Home.js'


import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';



function Home() {

    const [formValues, setFormValues] = useState({
        csvFile: "",
        valMethod: "file",
        valFile: "",
        //tablestyle: "black",
        dataenclosure: "",
        valenclosure: "",
        datadelimiter: ",",
        valdelimiter: ",",
    });

    const [isFormVisible, setIsFormVisible] = useState(true);

    const csvFileRef = useRef();
	const valMethodRef = useRef();
	const valFileRef = useRef();
	// const tablestyleRef = useRef();
	const dataenclosureRef = useRef();
	const valenclosureRef = useRef();
	const datadelimiterRef = useRef();
	const valdelimiterRef = useRef();



    const handleSubmit = (e) => {
        e.preventDefault();


		formValues.csvFile = csvFileRef?.current?.files;
		formValues.valMethod = valMethodRef?.current?.value;
		formValues.valFile = valFileRef?.current?.files;
		// formValues.tablestyle = tablestyleRef?.current?.files;
		formValues.dataenclosure = dataenclosureRef?.current?.value;
		formValues.valenclosure = valenclosureRef?.current?.value;
		formValues.datadelimiter = datadelimiterRef?.current?.value;
		formValues.valdelimiter = valdelimiterRef?.current?.value;

		//console.log(formValues);
		// console.log(csvFileRef?.current?.files);
		// console.log(valMethodRef?.current?.value);
		// console.log(valFileRef?.current?.files);
		// // console.log(tablestyleRef?.current?.value);
		// console.log(dataenclosureRef?.current?.value);
		// console.log(valenclosureRef?.current?.value);
		// console.log(datadelimiterRef?.current?.value);
		// console.log(valdelimiterRef?.current?.value);

        setIsFormVisible(false);
    };

	const handleGoHome = (e) => {
		setIsFormVisible(true);
	}

    return (
        <>
            {isFormVisible ? (
                <>
                <h2>HOME</h2>
                <form onSubmit={handleSubmit} className="text-start">

                    <div className="row mb-3">
                        <label className="col-3">File DATA<sub>(*.csv)</sub><span className="text-danger font-weight-bold">*</span>:</label>
                        <input type="file" name="csvFile" id="csvFile" className="col-9" required ref={csvFileRef} />
                    </div>


                    <div className="row mb-3">
                        <label className='col-3'>File VAL<sub>(*.csv)</sub>:</label>

                        <div id="valFileMethod" className="col-9">
                            <input type="file" name="valFile" id="valFile" ref={valFileRef} />
                            <a role="button" href="/val-generator">
                                <button type="button" className="btn btn-warning" id="go_home">
                                    <i className="bi bi-gear-fill"></i> Generate new one
                                    </button>
                            </a>
                        </div>

                        <input type="hidden" name="valMethod" />
                    </div>


                    {/* <div id="table_style_cont" className="row mb-3">
                        <label className='col-3'>Table result look</label>

                        <div className="col-9">
                            <input type="radio" name="tablestyle" id="light" value="light" /><label htmlFor="light" style={{ marginRight: "1em" }}>light</label>
                            <input type="radio" name="tablestyle" id="dark" value="dark" defaultChecked /><label htmlFor="dark">dark</label>
                        </div>
                    </div> */}


                    <div className="row">

                        <div className="mb-3 col-6">
                            <label><em>Enclosures</em></label>

                            <div className="row">
                                <label htmlFor="dataenclosure" className="col-3">Data file</label>
                                <input type="text" id="dataenclosure" name="dataenclosure" data-original-value="," required="" className="col-2" ref={csvFileRef}/>
                            </div>

                            <div className="row">
                                <label htmlFor="valenclosure" className="col-3">Val file</label>
                                <input type="text" id="valenclosure" name="valenclosure" data-original-value="," required="" className="col-2" data-default-readonly="false" ref={valenclosureRef}  />
                                <div id="lockEnclosures" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
                                    <i className="bi bi-lock-fill" ><span className="fs-6">locked</span></i>
                                    <i className="bi bi-unlock-fill ms-1"><span className="fs-6">unlocked</span></i>
                                </div>
                            </div>

                            <div id="enclosuresMsg"></div>
                        </div>


                        <div className="mb-3 col-6">
                            <label><em>Delimiters</em></label>

                            <div className="row">
                                <label htmlFor="datadelimiter" className="col-3">Data file</label>
                                <input type="text" id="datadelimiter" name="datadelimiter" data-original-value="," required="" className="col-2" ref={datadelimiterRef} />
                            </div>
                            <div className="row">
                                <label htmlFor="valdelimiter" className="col-3">Val file</label>
                                <input type="text" id="valdelimiter" name="valdelimiter" data-original-value="," required="" className="col-2" data-default-readonly="false" ref={valdelimiterRef} />
                                <div id="lockDelimiters" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
                                    <i className="bi bi-lock-fill" ><span className="fs-6">locked</span></i>
                                    <i className="bi bi-unlock-fill ms-1"><span className="fs-6">unlocked</span></i>
                                </div>
                            </div>

                            <div className="row" id="delimitersMsg"></div>
                        </div>

                    </div>

                    <div className="col-6">
                        <input type="submit" value="Validate" name="submit" className="btn btn-primary me-3" />
                        <input type="reset" value="Clear" className="resetter btn btn-secondary me-3" onClick={() => { setReset() }} />
                    </div>
                </form>
                </>
            ) : (
				<Validator data={formValues} handleGoHome={handleGoHome} />
				// <Validator data={formValues} />
            )}			
        </>
    );


}


export default Home;
