import * as React from "react";
import { useState } from 'react';
import ReactDOM from 'react-dom/client';

import { setReset } from '../Scripts/Home.js'

import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';








function MyForm() {
	const [name, setName] = useState("");

	return (
		<form>
			<label>Enter your name:
        <input
					type="text"
					value={name}
					onChange={(e) => setName(e.target.value)}
				/>
			</label>
		</form>
	)
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<MyForm />);




function reset() {
	setReset()
}

function Home() {
	const [name, setName] = useState("");
	const [csvFile, setCsvFile] = useState("");
	const [valMethod, setValMethod] = useState("");
	const [valFile, setValFile] = useState("");
	const [tablestyle, setTablestyle] = useState("");
	const [dataenclosure, setDataenclosure] = useState("");
	const [valenclosure, setValenclosure] = useState("");
	const [datadelimiter, setDatadelimiter] = useState("");
	const [valdelimiter, setValdelimiter] = useState("");




	return (
		<>
			<h2>HOME</h2>
			<form action="/validate" method="post" encType="multipart/form-data" id="indexform" name="indexform">

				<fieldset>
					<legend>File DATA<sub>(*.csv)</sub><span className="text-danger font-weight-bold">*</span>:</legend>
					<input type="file" name="csvFile" id="csvFile" value={csvFile} onChange={(e) => setCsvFile(e.target.value)} required />
				</fieldset>


				<fieldset>
					<legend>File VAL<sub>(*.csv)</sub>:</legend>

					<input type="hidden" name="valMethod" value="file" />

					<div id="valFileMethod">
						<input type="file" name="valFile" id="valFile" value={valFile} onChange={(e) => setValFile(e.target.value)} />
						<a role="button" href="/val-generator">
							<button type="button" className="btn btn-warning" id="go_home">
								<i className="bi bi-gear-fill"></i> Generate new one
                                </button>
						</a>
					</div>
				</fieldset>


				<fieldset id="table_style_cont">
					<legend>Table result look</legend>
					<input type="radio" name="tablestyle" id="light" value="light" /><label htmlFor="light" style={{ marginRight: "1em" }} >light</label>
					<input type="radio" name="tablestyle" id="dark" value="dark" defaultChecked /><label htmlFor="dark">dark</label>
				</fieldset>


				<fieldset>
					<legend>Enclosures</legend>
					<div className="row">
						<label htmlFor="dataenclosure" className="col-2">Data file</label>
						<input type="text" id="dataenclosure" name="dataenclosure" data-original-value="," required="" className="col-4" />
					</div>

					<div className="row">
						<label htmlFor="valenclosure" className="col-2">Val file</label>
						<input type="text" id="valenclosure" name="valenclosure" data-original-value="," required="" className="col-4" data-default-readonly="false" />
						<div id="lockEnclosures" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
							<i className="bi bi-lock-fill" ><span class="fs-6">locked</span></i>
							<i className="bi bi-unlock-fill ms-1"><span class="fs-6">unlocked</span></i>
						</div>
					</div>

					<div id="enclosuresMsg"></div>
				</fieldset>


				<fieldset>
					<legend>Delimiters</legend>
					<div className="row">
						<label htmlFor="datadelimiter" className="col-2">Data file</label>
						<input type="text" id="datadelimiter" name="datadelimiter" defaultValue="," data-original-value="," required="" className="col-4" />
					</div>
					<div className="row">
						<label htmlFor="valdelimiter" className="col-2">Val file</label>
						<input type="text" id="valdelimiter" name="valdelimiter" defaultValue="," data-original-value="," required="" className="col-4" data-default-readonly="false" />
						<div id="lockDelimiters" style={{ fontSize: "2em", marginTop: "-0.75em" }} data-default-open="false" role="button" className="ms-0 ps-0">
							<i className="bi bi-lock-fill" ><span class="fs-6">locked</span></i>
							<i className="bi bi-unlock-fill ms-1"><span class="fs-6">unlocked</span></i>
						</div>
					</div>

					<div className="row" id="delimitersMsg"></div>
				</fieldset>


				<div>
					<input type="submit" value="Validate" name="submit" className="btn btn-primary me-3" />
					<input type="reset" value="Clear" className="resetter btn btn-secondary me-3" onClick={() => { this.reset() }} />
				</div>
			</form>

		</>
	);


}


export default Home;
